mapper.update:
UPDATE charge_record
SET status = #{status}, charged_kwh = #{chargedKwh}, end_time = #{endTime}, duration_min = #{durationMin, javaType=float, jdbcType=FLOAT}, charging_cost = #{chargingCost}
WHERE record_id = #{recordId}

mapper.select:
SELECT record_id AS recordId, user_id AS userId, station_id AS stationId, status, start_kwh AS startKwh, target_kwh AS targetKwh, charged_kwh AS chargedKwh, start_time AS startTime, end_time AS endTime, duration_min AS durationMin, charging_cost AS chargingCost
FROM charge_record
WHERE record_id = #{recordId}
